const apiUrl="https://jsonplaceholder.typicode.com";function getPostById(t){return fetch(apiUrl+"/posts/"+t).then(t=>{if(t.ok)return t.json();throw new Error("Не удалось получить пост")})}function getCommentsByPostId(t){return fetch(apiUrl+`/posts/${t}/comments`).then(t=>{if(t.ok)return t.json();throw new Error("Не удалось получить комментарии")})}const searchButton=document.querySelector("#searchButton"),postContainer=document.querySelector("#postContainer");searchButton.addEventListener("click",()=>{const e=document.querySelector("#postId").value;getPostById(e).then(t=>{postContainer.innerHTML=`
        <h2>${t.title}</h2>
        <p>${t.body}</p>
        <button id="commentsButton">Показать комментарии</button>
      `,document.querySelector("#commentsButton").addEventListener("click",()=>{getCommentsByPostId(e).then(t=>{const o=document.createElement("ul");t.forEach(t=>{var e=document.createElement("li");e.textContent=t.body,o.appendChild(e)}),postContainer.appendChild(o)}).catch(t=>{console.error(t)})})}).catch(t=>{console.error(t),postContainer.innerHTML="<p>Пост не найден</p>"})});